mapper.select:
SELECT A.CALENDAR_ID AS calendarId , B.CALENDAR_DATE AS calendarDate , B.WORKING_FLAG AS workingFlag FROM TNM_CALENDAR_MASTER A INNER JOIN TNM_CALENDAR_DETAIL B ON B.CALENDAR_ID = A.CALENDAR_ID WHERE A.INACTIVE_FLAG = #{ACTIVE} AND B.CALENDAR_DATE > #{calendarDate}
AND A.OFFICE_ID IN
#{item}
ORDER BY A.CALENDAR_ID , B.CALENDAR_DATE

SELECT A.SHIPPING_ROUTE_CODE AS shippingRouteCode , A.IMP_CC_LEADTIME AS impCcLt , A.IMP_INBOUND_LEADTIME AS impInboundLt , B.DELIVERY_START AS deliveryStart , B.DELIVERY_END AS deliveryEnd , B.LAST_VANNING AS lastVanning , B.KANBAN_ISSUE_DATE AS kanbanIssueDate , B.EXP_INBOUND_DATE AS expInboundDate , B.ETD AS etd , B.ETA AS eta , B.SUPPLIER_ID AS supplierId FROM TNM_SR_MASTER A INNER JOIN TNM_SR_DETAIL B ON A.SR_ID = B.SR_ID WHERE A.INACTIVE_FLAG = #{ACTIVE} AND B.INACTIVE_FLAG = #{ACTIVE}
AND A.SHIPPING_ROUTE_TYPE IN
#{item}
AND B.DELIVERY_END >= #{deliveryEnd}
AND B.ETD >= #{etd}
AND B.KANBAN_ISSUE_DATE >= #{kanbanIssueDate}
AND B.EXP_INBOUND_DATE >= #{expInboundDate}
AND A.OFFICE_ID IN
#{item}
ORDER BY A.SHIPPING_ROUTE_CODE , B.SUPPLIER_ID , B.DELIVERY_START , B.DELIVERY_END , B.LAST_VANNING , B.KANBAN_ISSUE_DATE , B.EXP_INBOUND_DATE , B.ETD , B.ETA
